CREATE TABLE CM_USR COMMENT '사용자관리'
(
	CM_USR_SEQ bigint NOT NULL COMMENT '순번 : 고유 식별 값' UNIQUE,
	USR_ID varchar(64) NOT NULL COMMENT '사용자아이디 : 사용자 아이디, 전화번호를 아이디로 사용' UNIQUE,
	USR_TP varchar(1) DEFAULT '''U''' NOT NULL COMMENT '사용자구분 : U - 사용자, S - 경영주',
	PASSWD varchar(512) NOT NULL COMMENT 'PASSWD : 비밀번호',
	USR_NM varchar(128) NOT NULL COMMENT '사용자명',
	EMAIL varchar(128) COMMENT '이메일주소',
	NATI_CD varchar(3) COMMENT '국적코드 : 국적코드',	
	MARKET_AGREE_YN varchar(1) DEFAULT '''N''' NOT NULL COMMENT '마케팅동의여부 : 마케팅 동의 여부, Y / N',
	JOIN_YN varchar(1) DEFAULT '''Y''' NOT NULL COMMENT '가입여부',
	PASSWD_CHNG_TOKEN varchar(512) COMMENT '비밀번호 변경 토큰',
	RMKS varchar(512) COMMENT '비고 : 비고',
	REG_PGM_ID varchar(256) NOT NULL COMMENT '등록프로그램아이디',
	REG_USR_ID varchar(50) COMMENT '등록사용자아이디',
	REG_USR_IP varchar(64) NOT NULL COMMENT '등록사용자아이피',
	REG_DTTM datetime NOT NULL COMMENT '등록일시',
	UPD_PGM_ID varchar(256) NOT NULL COMMENT '수정프로그램아이디',
	UPD_USR_ID varchar(50) COMMENT '수정사용자아이디',
	UPD_USR_IP varchar(64) NOT NULL COMMENT '수정사용자아이피',
	UPD_DTTM datetime NOT NULL COMMENT '수정일시',
	PRIMARY KEY (CM_USR_SEQ)
);

CREATE TABLE CM_STOR COMMENT '점포관리'
(
	CM_STOR_SEQ bigint NOT NULL COMMENT '순번 : 고유 식별 값' UNIQUE,
	STOR_ID varchar(64) NOT NULL COMMENT '점포아이디 : 점포 아이디, 매장 전화번호를 아이디로 사용' UNIQUE,
	STOR_NM varchar(128) NOT NULL COMMENT '점포명 : 점포명',
	TEL_NO varchar(32) COMMENT '전화번호 : 매장전화번호',
	NATI_CD varchar(3) COMMENT '국적코드 : 국적코드',	
	ADDR_PT1 varchar(256) COMMENT '주소1 : 주소1',
	ADDR_PT2 varchar(256) COMMENT '주소2 : 주소2',
	ADDR_DTL varchar(256) COMMENT '상세 주소 : 상세 주소',
	ZIP_CD varchar(20) COMMENT '우편번호코드 : 우편번호 코드',	
	SAV_TP varchar(1) DEFAULT '''C''' NOT NULL COMMENT '적립구분 : 적립 구분, C : 쿠폰, P : 포인트',
	SAV_AMT bigint DEFAULT 0 COMMENT '적립량 : 적립량',
	SAV_LIMIT_AMT bigint DEFAULT 0 COMMENT '적립제한량 : 적립제한량',
	USE_AMT bigint DEFAULT 0 COMMENT '사용량 : 사용량( 해당 점포에서 고객이 사용한 양 - 집계 )',
	MAP_LAT varchar(20) COMMENT '지도에 표시할 위도 정보',
	MAP_LNG varchar(20) COMMENT '지도에 표시할 경도 정보',
	RMKS varchar(512) COMMENT '비고 : 비고',
	REG_PGM_ID varchar(256) NOT NULL COMMENT '등록프로그램아이디',
	REG_USR_ID varchar(50) COMMENT '등록사용자아이디',
	REG_USR_IP varchar(64) NOT NULL COMMENT '등록사용자아이피',
	REG_DTTM datetime NOT NULL COMMENT '등록일시',
	UPD_PGM_ID varchar(256) NOT NULL COMMENT '수정프로그램아이디',
	UPD_USR_ID varchar(50) COMMENT '수정사용자아이디',
	UPD_USR_IP varchar(64) NOT NULL COMMENT '수정사용자아이피',
	UPD_DTTM datetime NOT NULL COMMENT '수정일시',
	PRIMARY KEY (CM_STOR_SEQ)
);

